import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import * as userService from '../services/user.service';

const Profile: React.FC = () => {
  const { user } = useAuth();
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [selectedLevel, setSelectedLevel] = useState<'beginner' | 'intermediate' | 'advanced'>('beginner');
  const [message, setMessage] = useState({ text: '', type: '' });

  const languages = [
    { code: 'en', name: 'Английский' },
    { code: 'fr', name: 'Французский' },
    { code: 'de', name: 'Немецкий' },
    { code: 'es', name: 'Испанский' },
    { code: 'it', name: 'Итальянский' },
    { code: 'zh', name: 'Китайский' },
    { code: 'ja', name: 'Японский' },
    { code: 'ru', name: 'Русский' },
  ];

  const handleAddLanguage = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!selectedLanguage) {
      setMessage({ text: 'Пожалуйста, выберите язык', type: 'error' });
      return;
    }

    try {
      await userService.updateUserLanguage(selectedLanguage, selectedLevel);
      setMessage({ text: 'Язык успешно добавлен', type: 'success' });
    } catch (error) {
      setMessage({ text: 'Ошибка при добавлении языка', type: 'error' });
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Профиль</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <div className="md:col-span-1">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex flex-col items-center">
              <div className="w-24 h-24 bg-blue-100 rounded-full flex items-center justify-center mb-4">
                <span className="text-2xl font-bold text-blue-600">
                  {user?.name.charAt(0).toUpperCase()}
                </span>
              </div>
              <h2 className="text-xl font-semibold">{user?.name}</h2>
              <p className="text-gray-600 mb-4">{user?.email}</p>
              <p className="text-sm bg-blue-100 text-blue-800 px-2 py-1 rounded">
                {user?.role === 'admin' ? 'Администратор' : 'Пользователь'}
              </p>
            </div>
          </div>
        </div>
        
        <div className="md:col-span-2">
          <div className="bg-white p-6 rounded-lg shadow-md mb-8">
            <h2 className="text-xl font-semibold mb-4">Ваши языки</h2>
            {user?.languages && user.languages.length > 0 ? (
              <div className="space-y-4">
                {user.languages.map((lang, index) => (
                  <div key={index} className="flex justify-between items-center p-3 bg-gray-50 rounded">
                    <div>
                      <span className="font-medium capitalize">{lang.language}</span>
                      <span className="ml-2 text-sm text-gray-500">({lang.level})</span>
                    </div>
                    <div>
                      <span className="text-sm text-gray-600">Прогресс: {lang.progress}%</span>
                      <div className="w-32 h-2 bg-gray-200 rounded-full mt-1">
                        <div
                          className="h-2 bg-blue-600 rounded-full"
                          style={{ width: `${lang.progress}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500">У вас пока нет выбранных языков.</p>
            )}
          </div>
          
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-4">Добавить язык</h2>
            
            {message.text && (
              <div
                className={`p-3 mb-4 rounded ${
                  message.type === 'success' ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                }`}
              >
                {message.text}
              </div>
            )}
            
            <form onSubmit={handleAddLanguage}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="language">
                  Язык
                </label>
                <select
                  id="language"
                  className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  value={selectedLanguage}
                  onChange={(e) => setSelectedLanguage(e.target.value)}
                >
                  <option value="">Выберите язык</option>
                  {languages.map((lang) => (
                    <option key={lang.code} value={lang.code}>
                      {lang.name}
                    </option>
                  ))}
                </select>
              </div>
              
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Уровень владения
                </label>
                <div className="flex space-x-4">
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      className="form-radio"
                      name="level"
                      value="beginner"
                      checked={selectedLevel === 'beginner'}
                      onChange={() => setSelectedLevel('beginner')}
                    />
                    <span className="ml-2">Начинающий</span>
                  </label>
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      className="form-radio"
                      name="level"
                      value="intermediate"
                      checked={selectedLevel === 'intermediate'}
                      onChange={() => setSelectedLevel('intermediate')}
                    />
                    <span className="ml-2">Средний</span>
                  </label>
                  <label className="inline-flex items-center">
                    <input
                      type="radio"
                      className="form-radio"
                      name="level"
                      value="advanced"
                      checked={selectedLevel === 'advanced'}
                      onChange={() => setSelectedLevel('advanced')}
                    />
                    <span className="ml-2">Продвинутый</span>
                  </label>
                </div>
              </div>
              
              <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Добавить язык
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
