import api from './api';
import { ILesson, IApiResponse } from '../types';

// Получение всех уроков с пагинацией
export const getLessons = async (
  page = 1,
  limit = 20
): Promise<IApiResponse<ILesson[]>> => {
  const response = await api.get<IApiResponse<ILesson[]>>(
    `/lessons?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Получение урока по ID
export const getLesson = async (id: string): Promise<IApiResponse<ILesson>> => {
  const response = await api.get<IApiResponse<ILesson>>(`/lessons/${id}`);
  return response.data;
};

// Получение уроков по языку
export const getLessonsByLanguage = async (
  language: string,
  page = 1,
  limit = 20
): Promise<IApiResponse<ILesson[]>> => {
  const response = await api.get<IApiResponse<ILesson[]>>(
    `/lessons/language/${language}?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Получение уроков по уровню
export const getLessonsByLevel = async (
  level: string,
  page = 1,
  limit = 20
): Promise<IApiResponse<ILesson[]>> => {
  const response = await api.get<IApiResponse<ILesson[]>>(
    `/lessons/level/${level}?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Создание нового урока (только для админов)
export const createLesson = async (
  lessonData: Partial<ILesson>
): Promise<IApiResponse<ILesson>> => {
  const response = await api.post<IApiResponse<ILesson>>('/lessons', lessonData);
  return response.data;
};

// Обновление урока (только для админов)
export const updateLesson = async (
  id: string,
  lessonData: Partial<ILesson>
): Promise<IApiResponse<ILesson>> => {
  const response = await api.put<IApiResponse<ILesson>>(
    `/lessons/${id}`,
    lessonData
  );
  return response.data;
};

// Удаление урока (только для админов)
export const deleteLesson = async (id: string): Promise<IApiResponse<null>> => {
  const response = await api.delete<IApiResponse<null>>(`/lessons/${id}`);
  return response.data;
};
