import api from './api';
import { IWord, IApiResponse } from '../types';

// Получение всех слов с пагинацией
export const getWords = async (
  page = 1,
  limit = 50
): Promise<IApiResponse<IWord[]>> => {
  const response = await api.get<IApiResponse<IWord[]>>(
    `/words?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Получение слова по ID
export const getWord = async (id: string): Promise<IApiResponse<IWord>> => {
  const response = await api.get<IApiResponse<IWord>>(`/words/${id}`);
  return response.data;
};

// Получение слов по языку
export const getWordsByLanguage = async (
  language: string,
  page = 1,
  limit = 50
): Promise<IApiResponse<IWord[]>> => {
  const response = await api.get<IApiResponse<IWord[]>>(
    `/words/language/${language}?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Получение слов по категории
export const getWordsByCategory = async (
  category: string,
  page = 1,
  limit = 50
): Promise<IApiResponse<IWord[]>> => {
  const response = await api.get<IApiResponse<IWord[]>>(
    `/words/category/${category}?page=${page}&limit=${limit}`
  );
  return response.data;
};

// Создание нового слова (только для админов)
export const createWord = async (wordData: Partial<IWord>): Promise<IApiResponse<IWord>> => {
  const response = await api.post<IApiResponse<IWord>>('/words', wordData);
  return response.data;
};

// Обновление слова (только для админов)
export const updateWord = async (
  id: string,
  wordData: Partial<IWord>
): Promise<IApiResponse<IWord>> => {
  const response = await api.put<IApiResponse<IWord>>(`/words/${id}`, wordData);
  return response.data;
};

// Удаление слова (только для админов)
export const deleteWord = async (id: string): Promise<IApiResponse<null>> => {
  const response = await api.delete<IApiResponse<null>>(`/words/${id}`);
  return response.data;
};
