import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import * as wordService from '../services/word.service';
import { IWord } from '../types';

const WordDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [word, setWord] = useState<IWord | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchWord = async () => {
      try {
        setLoading(true);
        setError(null);
        
        if (!id) {
          setError('ID слова не указан');
          return;
        }
        
        const response = await wordService.getWord(id);
        
        if (response.success && response.data) {
          setWord(response.data);
        } else {
          setError('Не удалось загрузить слово');
        }
      } catch (err) {
        setError('Ошибка при загрузке слова');
      } finally {
        setLoading(false);
      }
    };
    
    fetchWord();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center py-16">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (error || !word) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error || 'Слово не найдено'}
        </div>
        <Link to="/words" className="text-blue-600 hover:text-blue-800">
          &larr; Вернуться к списку слов
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <Link to="/words" className="text-blue-600 hover:text-blue-800">
          &larr; Вернуться к списку слов
        </Link>
      </div>
      
      <div className="bg-white p-6 rounded-lg shadow-md">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
          <h1 className="text-3xl font-bold">{word.original}</h1>
          <div className="mt-2 md:mt-0">
            <span
              className={`px-3 py-1 rounded text-sm ${
                word.difficulty === 'easy'
                  ? 'bg-green-100 text-green-800'
                  : word.difficulty === 'medium'
                  ? 'bg-yellow-100 text-yellow-800'
                  : 'bg-red-100 text-red-800'
              }`}
            >
              {word.difficulty}
            </span>
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div>
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-2">Перевод</h2>
              <p className="text-2xl">{word.translation}</p>
            </div>
            
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-2">Информация</h2>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-gray-600">Язык:</p>
                  <p>{word.language}</p>
                </div>
                <div>
                  <p className="text-gray-600">Категория:</p>
                  <p>{word.category}</p>
                </div>
              </div>
            </div>
            
            {word.examples && word.examples.length > 0 && (
              <div className="mb-6">
                <h2 className="text-xl font-semibold mb-2">Примеры использования</h2>
                <ul className="list-disc pl-5 space-y-2">
                  {word.examples.map((example, index) => (
                    <li key={index}>{example}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
          
          <div>
            {word.imageUrl && (
              <div className="mb-6">
                <h2 className="text-xl font-semibold mb-2">Изображение</h2>
                <div className="rounded-lg overflow-hidden">
                  <img
                    src={word.imageUrl}
                    alt={word.original}
                    className="w-full h-auto object-cover"
                  />
                </div>
              </div>
            )}
            
            {word.audioUrl && (
              <div className="mb-6">
                <h2 className="text-xl font-semibold mb-2">Произношение</h2>
                <audio controls className="w-full">
                  <source src={word.audioUrl} type="audio/mpeg" />
                  Ваш браузер не поддерживает аудио элемент.
                </audio>
              </div>
            )}
            
            <div className="p-4 bg-blue-50 rounded-lg">
              <h2 className="text-xl font-semibold mb-2">Тренировка</h2>
              <p className="text-gray-700 mb-4">
                Добавьте это слово в свой список для изучения, чтобы тренировать его с помощью
                интервального повторения.
              </p>
              <button className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Добавить в изучаемые
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WordDetail;
