import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import * as lessonService from '../services/lesson.service';
import { ILesson } from '../types';

const LessonList: React.FC = () => {
  const [lessons, setLessons] = useState<ILesson[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedLanguage, setSelectedLanguage] = useState('');
  const [selectedLevel, setSelectedLevel] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const languageOptions = [
    { code: '', name: 'Все языки' },
    { code: 'en', name: 'Английский' },
    { code: 'fr', name: 'Французский' },
    { code: 'de', name: 'Немецкий' },
    { code: 'es', name: 'Испанский' },
    { code: 'it', name: 'Итальянский' },
    { code: 'zh', name: 'Китайский' },
    { code: 'ja', name: 'Японский' },
    { code: 'ru', name: 'Русский' },
  ];

  const levelOptions = [
    { code: '', name: 'Все уровни' },
    { code: 'beginner', name: 'Начинающий' },
    { code: 'intermediate', name: 'Средний' },
    { code: 'advanced', name: 'Продвинутый' },
  ];

  useEffect(() => {
    fetchLessons();
  }, [selectedLanguage, selectedLevel, page]);

  useEffect(() => {
    // Сброс страницы при изменении фильтров
    if (page !== 1) {
      setPage(1);
    } else {
      fetchLessons();
    }
  }, [selectedLanguage, selectedLevel]);

  const fetchLessons = async () => {
    try {
      setLoading(true);
      setError(null);

      let response;

      if (selectedLanguage && selectedLevel) {
        // Если выбраны и язык, и уровень, используем запрос по языку и фильтруем на клиенте
        response = await lessonService.getLessonsByLanguage(selectedLanguage, page);
        if (response.success && response.data) {
          setLessons(response.data.filter(lesson => lesson.level === selectedLevel));
        }
      } else if (selectedLanguage) {
        response = await lessonService.getLessonsByLanguage(selectedLanguage, page);
        if (response.success && response.data) {
          setLessons(response.data);
        }
      } else if (selectedLevel) {
        response = await lessonService.getLessonsByLevel(selectedLevel, page);
        if (response.success && response.data) {
          setLessons(response.data);
        }
      } else {
        response = await lessonService.getLessons(page);
        if (response.success && response.data) {
          setLessons(response.data);
        }
      }

      if (response.success) {
        if (response.pagination) {
          setTotalPages(response.pagination.pages);
        }
      } else {
        setError(response.message || 'Ошибка при загрузке уроков');
      }
    } catch (error: any) {
      setError(error.message || 'Ошибка при загрузке уроков');
    } finally {
      setLoading(false);
    }
  };

  const handleLanguageChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedLanguage(e.target.value);
  };

  const handleLevelChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedLevel(e.target.value);
  };

  const handlePageChange = (page: number) => {
    setPage(page);
  };

  const renderPagination = () => {
    const pages = [];
    for (let i = 1; i <= totalPages; i++) {
      pages.push(
        <button
          key={i}
          onClick={() => handlePageChange(i)}
          className={`px-3 py-1 mx-1 rounded ${
            page === i
              ? 'bg-blue-600 text-white'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          {i}
        </button>
      );
    }
    return (
      <div className="flex justify-center mt-6">
        <button
          onClick={() => handlePageChange(page - 1)}
          disabled={page === 1}
          className={`px-3 py-1 mx-1 rounded ${
            page === 1
              ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          &laquo;
        </button>
        {pages}
        <button
          onClick={() => handlePageChange(page + 1)}
          disabled={page === totalPages}
          className={`px-3 py-1 mx-1 rounded ${
            page === totalPages
              ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          &raquo;
        </button>
      </div>
    );
  };

  const getLevelBadgeClass = (level: string) => {
    switch (level) {
      case 'beginner':
        return 'bg-green-100 text-green-800';
      case 'intermediate':
        return 'bg-yellow-100 text-yellow-800';
      case 'advanced':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getLevelName = (level: string) => {
    switch (level) {
      case 'beginner':
        return 'Начинающий';
      case 'intermediate':
        return 'Средний';
      case 'advanced':
        return 'Продвинутый';
      default:
        return level;
    }
  };

  const getLanguageName = (code: string) => {
    return languageOptions.find(lang => lang.code === code)?.name || code;
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Уроки</h1>

      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="language">
              Язык
            </label>
            <select
              id="language"
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={selectedLanguage}
              onChange={handleLanguageChange}
            >
              {languageOptions.map((lang) => (
                <option key={lang.code} value={lang.code}>
                  {lang.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="level">
              Уровень
            </label>
            <select
              id="level"
              className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={selectedLevel}
              onChange={handleLevelChange}
            >
              {levelOptions.map((level) => (
                <option key={level.code} value={level.code}>
                  {level.name}
                </option>
              ))}
            </select>
          </div>
        </div>

        {loading ? (
          <div className="flex justify-center py-8">
            <div className="w-12 h-12 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
          </div>
        ) : error ? (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {error}
          </div>
        ) : lessons.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            Уроки не найдены. Попробуйте изменить фильтры.
          </div>
        ) : (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {lessons.map((lesson) => (
                <div key={lesson._id} className="bg-white border rounded-lg shadow-sm overflow-hidden hover:shadow-md transition-shadow">
                  {lesson.imageUrl ? (
                    <div className="h-40 overflow-hidden">
                      <img
                        src={lesson.imageUrl}
                        alt={lesson.title}
                        className="w-full h-full object-cover"
                      />
                    </div>
                  ) : (
                    <div className="h-40 bg-blue-50 flex items-center justify-center">
                      <span className="text-blue-500 text-4xl">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-16 w-16" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                        </svg>
                      </span>
                    </div>
                  )}
                  <div className="p-4">
                    <div className="flex justify-between items-start mb-2">
                      <h2 className="text-xl font-semibold">{lesson.title}</h2>
                      <span
                        className={`px-2 py-1 rounded text-xs font-medium ${getLevelBadgeClass(lesson.level)}`}
                      >
                        {getLevelName(lesson.level)}
                      </span>
                    </div>
                    <p className="text-gray-600 mb-4 line-clamp-2">{lesson.description}</p>
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-500">
                        {getLanguageName(lesson.language)}
                      </span>
                      <span className="text-sm text-gray-500">
                        {Array.isArray(lesson.words) ? lesson.words.length : 0} слов
                      </span>
                    </div>
                    <div className="mt-4">
                      <Link
                        to={`/lessons/${lesson._id}`}
                        className="block w-full text-center bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded transition-colors"
                      >
                        Начать урок
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            {totalPages > 1 && renderPagination()}
          </>
        )}
      </div>
    </div>
  );
};

export default LessonList;
