import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import * as lessonService from '../services/lesson.service';
import { ILesson, IWord } from '../types';

const LessonDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [lesson, setLesson] = useState<ILesson | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showTranslation, setShowTranslation] = useState(false);
  const [lessonCompleted, setLessonCompleted] = useState(false);

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        setLoading(true);
        setError(null);
        
        if (!id) {
          setError('ID урока не указан');
          return;
        }
        
        const response = await lessonService.getLesson(id);
        
        if (response.success && response.data) {
          setLesson(response.data);
        } else {
          setError('Не удалось загрузить урок');
        }
      } catch (err) {
        setError('Ошибка при загрузке урока');
      } finally {
        setLoading(false);
      }
    };
    
    fetchLesson();
  }, [id]);

  const handleNextWord = () => {
    if (lesson && Array.isArray(lesson.words)) {
      if (currentWordIndex < lesson.words.length - 1) {
        setCurrentWordIndex(currentWordIndex + 1);
        setShowTranslation(false);
      } else {
        setLessonCompleted(true);
      }
    }
  };

  const handlePrevWord = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowTranslation(false);
    }
  };

  const toggleTranslation = () => {
    setShowTranslation(!showTranslation);
  };

  const resetLesson = () => {
    setCurrentWordIndex(0);
    setShowTranslation(false);
    setLessonCompleted(false);
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center py-16">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (error || !lesson) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error || 'Урок не найден'}
        </div>
        <Link to="/lessons" className="text-blue-600 hover:text-blue-800">
          &larr; Вернуться к списку уроков
        </Link>
      </div>
    );
  }

  const currentWord = Array.isArray(lesson.words) && lesson.words.length > 0
    ? lesson.words[currentWordIndex] as IWord
    : null;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <Link to="/lessons" className="text-blue-600 hover:text-blue-800">
          &larr; Вернуться к списку уроков
        </Link>
      </div>
      
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <div className="mb-6">
          <h1 className="text-3xl font-bold mb-2">{lesson.title}</h1>
          <div className="flex items-center space-x-4 text-sm text-gray-600">
            <span>Язык: {lesson.language}</span>
            <span>Уровень: {lesson.level}</span>
            <span>Категория: {lesson.category}</span>
          </div>
        </div>
        
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-2">Описание</h2>
          <p className="text-gray-700">{lesson.description}</p>
        </div>
        
        {lessonCompleted ? (
          <div className="text-center py-8">
            <div className="mb-4">
              <svg
                className="w-16 h-16 text-green-500 mx-auto"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                />
              </svg>
            </div>
            <h2 className="text-2xl font-bold mb-2">Урок завершен!</h2>
            <p className="text-gray-600 mb-6">
              Вы успешно прошли все слова в этом уроке.
            </p>
            <div className="flex justify-center space-x-4">
              <button
                onClick={resetLesson}
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Повторить урок
              </button>
              <Link
                to="/lessons"
                className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                К списку уроков
              </Link>
            </div>
          </div>
        ) : !currentWord ? (
          <div className="text-center py-8 text-gray-500">
            В этом уроке пока нет слов.
          </div>
        ) : (
          <div>
            <div className="mb-4 flex justify-between items-center">
              <h2 className="text-xl font-semibold">
                Слово {currentWordIndex + 1} из {Array.isArray(lesson.words) ? lesson.words.length : 0}
              </h2>
              <div className="w-48 h-2 bg-gray-200 rounded-full">
                <div
                  className="h-2 bg-blue-600 rounded-full"
                  style={{
                    width: `${
                      ((currentWordIndex + 1) / (Array.isArray(lesson.words) ? lesson.words.length : 1)) * 100
                    }%`,
                  }}
                ></div>
              </div>
            </div>
            
            <div className="bg-blue-50 p-8 rounded-lg mb-6 text-center">
              <div className="mb-6">
                <h3 className="text-3xl font-bold mb-2">{currentWord.original}</h3>
                {showTranslation && (
                  <p className="text-xl text-gray-700">{currentWord.translation}</p>
                )}
              </div>
              
              {currentWord.examples && currentWord.examples.length > 0 && (
                <div className="mb-4 text-left">
                  <h4 className="font-semibold mb-2">Примеры:</h4>
                  <ul className="list-disc pl-5 space-y-1 text-gray-700">
                    {currentWord.examples.map((example, index) => (
                      <li key={index}>{example}</li>
                    ))}
                  </ul>
                </div>
              )}
              
              <button
                onClick={toggleTranslation}
                className="bg-white border border-blue-500 text-blue-600 hover:bg-blue-50 font-medium py-2 px-4 rounded mb-4"
              >
                {showTranslation ? 'Скрыть перевод' : 'Показать перевод'}
              </button>
            </div>
            
            <div className="flex justify-between">
              <button
                onClick={handlePrevWord}
                disabled={currentWordIndex === 0}
                className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline disabled:opacity-50"
              >
                &larr; Предыдущее
              </button>
              <button
                onClick={handleNextWord}
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                {currentWordIndex === (Array.isArray(lesson.words) ? lesson.words.length - 1 : 0)
                  ? 'Завершить урок'
                  : 'Следующее &rarr;'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LessonDetail;
